    public void testGetToken() throws Exception {
        FileReader program1FileReader = new FileReader("testResources/program1.cm");
        Scanner program1Scanner = new Scanner(program1FileReader);

        List<Token.Type> expectedTokens = new ArrayList<>();

        /* int x; int y;
           int z[10]; */
        expectedTokens.add(Token.Type.INT_TYPE);
        expectedTokens.add(Token.Type.ID);
        expectedTokens.add(Token.Type.SEMI);
        expectedTokens.add(Token.Type.INT_TYPE);
        expectedTokens.add(Token.Type.ID);
        expectedTokens.add(Token.Type.SEMI);

        /* int z[10]; */
        expectedTokens.add(Token.Type.INT_TYPE);
        expectedTokens.add(Token.Type.ID);
        expectedTokens.add(Token.Type.LBRACE);
        expectedTokens.add(Token.Type.INT);
        expectedTokens.add(Token.Type.RBRACE);
        expectedTokens.add(Token.Type.SEMI);

        /* void f( void ) { */
        expectedTokens.add(Token.Type.VOID);
        expectedTokens.add(Token.Type.ID);
        expectedTokens.add(Token.Type.LPAREN);
        expectedTokens.add(Token.Type.VOID);
        expectedTokens.add(Token.Type.RPAREN);
        expectedTokens.add(Token.Type.LBRACE_CURLY);

        /* int i;
           i = 0; */
        expectedTokens.add(Token.Type.INT_TYPE);
        expectedTokens.add(Token.Type.ID);
        expectedTokens.add(Token.Type.SEMI);
        expectedTokens.add(Token.Type.ID);
        expectedTokens.add(Token.Type.ASSIGN);
        expectedTokens.add(Token.Type.INT);
        expectedTokens.add(Token.Type.SEMI);

        /* while( i < 10 ) { */
        expectedTokens.add(Token.Type.WHILE);
        expectedTokens.add(Token.Type.LPAREN);
        expectedTokens.add(Token.Type.ID);
        expectedTokens.add(Token.Type.LT);
        expectedTokens.add(Token.Type.INT);
        expectedTokens.add(Token.Type.RPAREN);
        expectedTokens.add(Token.Type.LBRACE_CURLY);

        /* z[ i ] = 100 + 2 * i; */
        expectedTokens.add(Token.Type.ID);
        expectedTokens.add(Token.Type.LBRACE);
        expectedTokens.add(Token.Type.ID);
        expectedTokens.add(Token.Type.RBRACE);
        expectedTokens.add(Token.Type.ASSIGN);
        expectedTokens.add(Token.Type.INT);
        expectedTokens.add(Token.Type.PLUS);
        expectedTokens.add(Token.Type.INT);
        expectedTokens.add(Token.Type.TIMES);
        expectedTokens.add(Token.Type.ID);
        expectedTokens.add(Token.Type.SEMI);

        /* i = i + 1; } } */
        expectedTokens.addAll(Arrays.asList(
                Token.Type.ID, Token.Type.ASSIGN, Token.Type.ID, Token.Type.PLUS, Token.Type.INT,
                Token.Type.SEMI, Token.Type.RBRACE_CURLY, Token.Type.RBRACE_CURLY
        ));

        /* void g( void ) { */
        expectedTokens.addAll(Arrays.asList(
                Token.Type.VOID, Token.Type.ID, Token.Type.LPAREN, Token.Type.VOID, Token.Type.RPAREN,
                Token.Type.LBRACE_CURLY
        ));

        /* int i;
           i = 0; */
        expectedTokens.addAll(Arrays.asList(
                Token.Type.INT_TYPE, Token.Type.ID, Token.Type.SEMI,
                Token.Type.ID, Token.Type.ASSIGN, Token.Type.INT, Token.Type.SEMI
        ));

        /* while( i < 10 ) { */
        expectedTokens.addAll(Arrays.asList(
                Token.Type.WHILE, Token.Type.LPAREN, Token.Type.ID, Token.Type.LT,
                Token.Type.INT, Token.Type.RPAREN, Token.Type.LBRACE_CURLY
        ));

        /* write z[ i ]; */
        expectedTokens.addAll(Arrays.asList(
                Token.Type.WRITE, Token.Type.ID, Token.Type.LBRACE, Token.Type.ID,
                Token.Type.RBRACE, Token.Type.SEMI
        ));

        /* i = i + 1; } } */
        expectedTokens.addAll(Arrays.asList(
                Token.Type.ID, Token.Type.ASSIGN, Token.Type.ID, Token.Type.PLUS, Token.Type.INT,
                Token.Type.SEMI, Token.Type.RBRACE_CURLY, Token.Type.RBRACE_CURLY
        ));

        /* void main( void ) { */
        expectedTokens.addAll(Arrays.asList(
                Token.Type.VOID, Token.Type.ID, Token.Type.LPAREN, Token.Type.VOID,
                Token.Type.RPAREN, Token.Type.LBRACE_CURLY
        ));

        /* f();
           g(); }
        */
        expectedTokens.addAll(Arrays.asList(
                Token.Type.ID, Token.Type.LPAREN, Token.Type.RPAREN, Token.Type.SEMI,
                Token.Type.ID, Token.Type.LPAREN, Token.Type.RPAREN, Token.Type.SEMI, Token.Type.RBRACE_CURLY
        ));

        for (Token.Type tokenType: expectedTokens) {
            Token.Type actualTokenType = program1Scanner.getToken().getType();
//            Generic.Utils.printToken(actualToken, "tokenString");
            assertEquals(tokenType, actualTokenType);
        }

//        Type actualToken = program1Scanner.getToken();
//        assertEquals(Type.END, actualToken);
    }



    public void testTokenName() throws FileNotFoundException {
        FileReader program1FileReader = new FileReader("testResources/program1.cm");
        Scanner program1Scanner = new Scanner(program1FileReader);

        List<String> expectedIdentifiers = new ArrayList<>(Arrays.asList("x", "y", "z", "f", "i", "i", "i", "z", "i", "i", "i", "i", "g", "i", "i", "i", "z", "i", "i", "i", "main", "f", "g", "i", "z", "i", "i", "i", "i", "g", "i", "i", "i", "z", "i", "i"));

        Token token = new Token();

        int i = 0;
        while (token.getType() != Token.Type.ENDFILE) {
            token = program1Scanner.getToken();
            if (token.getName() != null
                    && !token.getName().isEmpty()
                    && token.getType() == Token.Type.ID) {
                System.out.println(token.getName());
                assertEquals(expectedIdentifiers.get(i), token.getName());
                i++;
            }
        }
    }

